package controller.parameters.listeners.action;

import gui.editor.dialogs.PanelDialog;

import java.awt.event.ActionEvent;
import java.awt.event.ActionListener;

import javax.swing.JFrame;

public class PanelDialogListener implements ActionListener{
	private JFrame parent;
	
	public PanelDialogListener(JFrame parent) {
		this.parent = parent;
	}
	@Override
	public void actionPerformed(ActionEvent arg0) {
		// TODO Auto-generated method stub
		PanelDialog dialog = new PanelDialog(parent);
		dialog.setVisible(true);
		
	}

}
